import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { Login } from './Login';
import { Register } from './Register';

function App() {
  const [currentForm, setCurrentForm] = useState('login');

  const toggleForm = (formName) => {
    setCurrentForm(formName);
  }

  const handleLogin = async (credentials) => {
    try {
      const response = await fetch('http://localhost:1337/auth/local', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          identifier: credentials.username,
          password: credentials.password
        })
      });

      const data = await response.json();

      // Guarda el token de acceso en el almacenamiento local o en el estado de tu aplicación
      const token = data.jwt;
      // Realiza cualquier acción adicional después de iniciar sesión exitosamente
    } catch (error) {
      // Maneja los errores de inicio de sesión
    }
  }

  const handleRegister = async (userData) => {
    try {
      const response = await fetch('http://localhost:1337/auth/local/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          username: userData.username,
          email: userData.email,
          password: userData.password
        })
      });

      // Realiza cualquier acción adicional después de registrar exitosamente al usuario
    } catch (error) {
      // Maneja los errores de registro
    }
  }

  return (
    <div className="App">
      {
        currentForm === "login" ? <Login onFormSwitch={toggleForm} onLogin={handleLogin} /> : <Register onFormSwitch={toggleForm} onRegister={handleRegister} />
      }
    </div>
  );
}

export default App;
